import React from 'react';
import styled from 'styled-components';

const gridColor = '#29e2f3';
const dashWidth = '2px';
const dashSpacing = '10px';
const redColor = 'red';

const Grid = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  margin: auto;

  background-image: repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    );
  background-size: 100px 100px;
  background-repeat: repeat;
  background-position: 100% 100%;
  pointer-events: none;
  opacity: 0.5;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 2px; /* 設置虛線寬度，包含間隔 */
    background-color: ${redColor};
    background-image: repeating-linear-gradient(
      to right,
      transparent 0,
      transparent 2px,
      /* 設置虛線間隔 */ ${redColor} 2px,
      /* 設置虛線顏色 */ ${redColor} 4px /* 設置虛線顏色和間隔的總和 */
    );
  }

  /* 添加水平虛線 */
  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: ${dashWidth};
    background: linear-gradient(to right, transparent 50%, red, 50%);
    background-size: ${dashWidth} ${dashWidth};
    background-repeat: repeat-x;
    transform: translateY(-50%);
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    width: ${dashWidth};
    height: 100%;
    background-color: red;
    background-image: repeating-linear-gradient(
      to bottom,
      transparent 0,
      transparent ${dashWidth},
      red ${dashWidth},
      red ${parseInt(dashWidth) + parseInt(dashSpacing)}
    );
  }
`;

export default function GridLine() {
  return <Grid />;
}
