import studio from '@theatre/studio';
import { val, getProject } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Stage from '../components/stage';
// import Dimmer from '../components/dimmer-with-message';

const ScrollSizer = styled.div`
  position: relative;
  width: 100vw;
  min-width: 100vw;
  max-width: 100vw;
  min-height: 100vh;
  background: #ffffff;
`;

const StickyBox = styled.div`
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
`;

const defaultScrollScale = 1500;

export default function DemoScroll() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // --------------------------
  // TODO: 增加條件，減少 event-listener 偵測時間

  const [scrollPosition, setScrollPosition] = useState(0);

  const sequenceLength = val(sheet.sequence.pointer.length);
  const stageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const stage = stageRef.current;
      const viewbox = stage.closest('.theatre-scroll-sizer');

      const stageRect = stage.getBoundingClientRect();
      const viewboxRect = viewbox.getBoundingClientRect();

      const distance = stageRect.top - viewboxRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const newPosition =
      (scrollPosition / (sequenceLength * defaultScrollScale)) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  const objJson = localStorage.getItem('theatre-data'); // get objectJSON in local-storage
  const objData = objJson ? JSON.parse(objJson) : [];

  // ----------------------------

  return (
    <ScrollSizer
      className='theatre-scroll-sizer'
      style={{ height: `${sequenceLength * defaultScrollScale}px` }}
    >
      <StickyBox ref={stageRef}>
        <Stage
          objData={objData}
          sheet={sheet}
          stageWidth={window.innerWidth}
          draggable={false}
        />
      </StickyBox>
    </ScrollSizer>
  );
}
