import React, { useState } from 'react';
import ElementButtons from './button/element-button';
import SettingButtons from './button/setting-button';
import FontLightBox from './lightbox/add-font';
import JSONLightBox from './lightbox/add-json';
import BgLightBox from './lightbox/add-bg';
import ImageLightBox from './lightbox/add-image';
import styled from 'styled-components';
import { getLocalStorageData } from '../utils/index';

const LightBoxWrapper = styled.div`
  position: relative;
`;

const ButtonWrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  z-index: 100;
`;

export default function ControlPanel({ sheet, setStoredData, storedData }) {
  const [showFontLightBox, setShowFontLightBox] = useState(false);
  const [showJSONLightBox, setShowJSONLightBox] = useState(false);
  const [showImageLightBox, setShowImageLightBox] = useState(false);
  const [showBgLightBox, setShowBgLightBox] = useState(false);

  const animate = getLocalStorageData('theatre-0.4.persistent');
  const animateJSONState =
    JSON.parse(animate)?.historic?.innerState?.coreByProject?.Project ?? {};

  const [animateJSON, setAnimateJSON] = useState(animateJSONState);

  const [elementJSON, setElementJSON] = useState(
    getLocalStorageData('theatre-data')
  );

  return (
    <>
      <LightBoxWrapper id='lightbox-wrapper'>
        {showFontLightBox && (
          <FontLightBox
            setShowFontLightBox={setShowFontLightBox}
            setStoredData={setStoredData}
            storedData={storedData}
          />
        )}

        {showImageLightBox && (
          <ImageLightBox
            setShowImageLightBox={setShowImageLightBox}
            storedData={storedData}
            setStoredData={setStoredData}
          />
        )}

        {showBgLightBox && (
          <BgLightBox
            setShowBgLightBox={setShowBgLightBox}
            storedData={storedData}
            setStoredData={setStoredData}
          />
        )}

        {showJSONLightBox && (
          <JSONLightBox
            setShowJSONLightBox={setShowJSONLightBox}
            setAnimateJSON={setAnimateJSON}
            animateJSON={animateJSON}
            elementJSON={elementJSON}
            setElementJSON={setElementJSON}
          />
        )}
      </LightBoxWrapper>

      <ButtonWrapper>
        <SettingButtons
          sheet={sheet}
          setShowJSONLightBox={setShowJSONLightBox}
          setElementJSON={setElementJSON}
          setAnimateJSON={setAnimateJSON}
        />
        <ElementButtons
          setShowFontLightBox={setShowFontLightBox}
          setShowImageLightBox={setShowImageLightBox}
          setShowBgLightBox={setShowBgLightBox}
        />
      </ButtonWrapper>
    </>
  );
}
