import studio from '@theatre/studio';
import { val, getProject } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import TheatreElements from '../components/theatre-elements';
import Stage from '../components/stage';

const ViewBox = styled.div`
  position: relative;
  width: 100%;
  min-height: 100vh;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
`;

const defaultScrollScale = 1500;

export default function DemoScroll() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // --------------------------
  // TODO: 增加條件，減少 event-listener 偵測時間

  const [scrollPosition, setScrollPosition] = useState(0);

  const sequenceLength = val(sheet.sequence.pointer.length); //總長度
  const stageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const stage = stageRef.current;
      const viewbox = stage.closest('.theatre-scroll-viewbox');

      const stageRect = stage.getBoundingClientRect();
      const viewboxRect = viewbox.getBoundingClientRect();

      const distance = stageRect.top - viewboxRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const newPosition =
      (scrollPosition / (sequenceLength * defaultScrollScale)) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  // ----- 上面這段是絕對可用的，勿動 --------------------------------

  const [storedData, setStoredData] = useState([]); // get data in local-storage

  useEffect(() => {
    const elements = localStorage.getItem('theatre-data');
    if (elements) {
      setStoredData(JSON.parse(elements));
    }
  }, []);

  useEffect(() => {
    if (storedData.length > 0) {
      renderFontObject(storedData, sheet);
      renderImageObject(storedData, sheet);
    }
  }, [storedData]);

  return (
    <ViewBox
      className='theatre-scroll-viewbox'
      style={{ height: `${sequenceLength * defaultScrollScale}px` }}
    >
      <Stage ref={stageRef} />
      {/* <TheatreContainer id='theatre-stage' ref={stageRef}>
        <TheatreElements sheet={sheet} />
      </TheatreContainer> */}
    </ViewBox>
  );
}
