import studio from '@theatre/studio';
import { useState, useEffect } from 'react';
import Stage from '../components/stage';
import EditPanel from '../components/edit-panel';
import { getProject } from '@theatre/core';
import { renderFontObject, renderBgObject, renderImageObject } from '../utils';
import GridLine from '../components/grid-line';
import RangeSlider from '../components/range-slider';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
  position: relative;
  outline: 2px solid #29e2f3;
  margin: auto;
`;
export default function Playground() {
  const [divRef, setDivRef] = useState(null);

  // ------------------------

  studio.initialize();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // -------------------------

  // 可以只拿初始值，然後隨時偵測更新，有更新時，更新 storedData 即時渲染畫面，同時直接存進 local-storage 裡
  const initialObjJson = localStorage.getItem('theatre-data'); // get object JSON in local-storage
  const initialObjData = initialObjJson ? JSON.parse(initialObjJson) : [];

  const [objData, setObjData] = useState(initialObjData);

  useEffect(() => {
    if (objData.length > 0) {
      // renderFontObject(objData, sheet, divRef);
      renderImageObject(objData, sheet, divRef);
      renderBgObject(objData, sheet, divRef);
    }
  }, [objData]);

  // ------------------------

  // Grid & Panel: keyboard shortcut setting
  const [showGrid, setShowGrid] = useState(true);
  const [showPanel, setShowPanel] = useState(true);

  studio.ui[showPanel ? 'restore' : 'hide']();

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'Escape') {
        setShowPanel(!showPanel);
      }
      if (event.key === 'Backquote' || event.key === '`') {
        setShowGrid(!showGrid);
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  });

  const [stageSize, setStageSize] = useState(null);

  const stageWidth = stageSize ? stageSize + 'px' : '100vw';

  return (
    <>
      <EditPanel
        sheet={sheet}
        setObjData={setObjData}
        objData={objData}
        showPanel={showPanel}
      />

      {/* <RangeSlider setStageSize={setStageSize} /> */}

      <Wrapper style={{ maxWidth: stageWidth }}>
        <Stage objData={objData} sheet={sheet} setDivRef={setDivRef} />
        {showGrid && <GridLine />}
      </Wrapper>
    </>
  );
}
