import styled from 'styled-components';
import React, { useState } from 'react';
import { types } from '@theatre/core';
import { initialProperties } from '../../constants';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 0.9;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({
  setShowFontLightBox,
  setNewData,
  sheet,
}) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  const addFontElement = (fontID = '', fontContent = '') => {
    const box = sheet.object(`${fontID}`, initialProperties.FONT);

    const div = document.createElement('div');
    div.textContent = `${fontContent}`;
    div.id = `${fontID}`;

    const container = document.getElementById('theatre-stage');
    if (container) {
      container.appendChild(box);
    }

    div.style.cssText = `
      font-size: 50px;
      position: absolute;
      color: black;
      z-index: 10000000;
    `;

    let style = {};

    controlMenu.onValuesChange((newValue) => {
      console.log('newValue', newValue);
      element.style.left = newValue.position.x + 'px';
      element.style.top = newValue.position.y + 'px';
      element.style.background = newValue.bgColor;
      element.style.color = newValue.font.color;
      element.style.opacity = newValue.opacity;
      element.style.padding = newValue.padding + 'px';
      element.style.display = newValue.visible ? 'block' : 'none';
      element.style.width = newValue.size.width + 'px';
      element.style.height = newValue.size.height + 'px';
      element.style.fontSize = newValue.font.size + 'px';
    });

    console.log(' element.style.left ', element.style.left);
    console.log(' element.style.height ', element.style.height);

    console.log('element', element);

    setNewData((prevData) => [
      ...prevData,
      {
        content: fontContent,
        element: element,
        id: fontID,
        type: 'font',
        style: element.style,
      },
    ]);
  };

  const addFont = (name = '', content = '') => {
    const box = sheet.object(`${name}`, {
      position: {
        x: 0,
        y: 0,
      },
      size: types.string('50px'),
      color: types.string('#000000'),
    });

    const div = document.createElement('div');

    div.style.cssText = `
      font-size: 50px;
      position: absolute;
      color: black;
      z-index: 10000000;
    `;

    div.textContent = `${content}`;

    document.body.appendChild(div);

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.fontSize = newValue.size;
      div.style.color = newValue.color;
    });
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID, fontContent);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
