import studio from '@theatre/studio';
import { getProject, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import TheatreElements from '../components/theatre-elements';
import { useInView } from 'react-intersection-observer';

const defaultViewportHeight = '100vh';

const ViewBox = styled.div`
  position: relative;
  width: 100%;
  height: 3000px;
  min-height: 100vh;
  outline: 3px solid blue;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100vh;
  outline: 1px solid red;

  position: sticky;
  top: 0;
  left: 0;
`;

// 下面參考 scrollable-video ---------------------------------

//代表滑到這個框框的最上方
const PositionStarter = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const PositionFinisher = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const Other = styled.div`
  width: 100%;
  height: 100vh;
`;

const Test = styled.div`
  width: 25px;
  height: 25px;
  outline: 1px solid red;
  position: sticky;
  top: 0;
  left: 0;
  background: red;
`;

export default function DemoScroll({ project, sheet }) {
  studio.ui.hide();
  project.ready.then(() => sheet.sequence.pause());

  const sequenceLength = val(sheet.sequence.pointer.length);

  // --------------------------------------------------------

  const testRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  console.log('scrollPosition', scrollPosition);

  // TODO: 增加條件，減少 event-listener 偵測時間

  useEffect(() => {
    const handleScroll = () => {
      const testElement = testRef.current;
      const viewboxElement = testElement.closest('.ViewBox');

      const testRect = testElement.getBoundingClientRect();
      const viewboxRect = viewboxElement.getBoundingClientRect();

      const distance = testRect.top - viewboxRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    // 3000 等同於 ScrollHeight 元件的高度
    const newPosition = (scrollPosition / 3000) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  // ----- 上面這段是絕對可用的，勿動 --------------------------------

  return (
    <>
      <Other />

      <ViewBox className='ViewBox'>
        <TheatreContainer id='theatre-container' ref={testRef}>
          <TheatreElements sheet={sheet} />
        </TheatreContainer>
      </ViewBox>

      <Other />
    </>
  );
}
