import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import EmbeddedCode from '../embedded';
import styled from 'styled-components';
import EmbeddedBox from '../components/embedded-box';
import FontBox from '../components/font-box';
import ReactDOM from 'react-dom';
import Font from '../components/font';
import projectState from '../json/state.json';
// import embedCodeGen from '@readr-media/react-embed-code-generator';

const BasicWrapper = styled.div`
  position: fixed;
  padding: 30px;
  bottom: 0;
  left: 0;
  background: pink;
`;

const AddWrapper = styled.div`
  position: fixed;
  padding: 30px;
  bottom: 0;
  left: 250px;
  background: #c1dafe;
`;

const AddButton = styled.div`
  padding: 5px;
  background: white;
  color: black;
  cursor: pointer;
  font-size: 14px;
  margin: 10px auto;

  &:hover {
    cursor: pointer;
  }
`;

const Play = styled.div`
  padding: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: white;
  margin: 10px;

  &:hover {
    cursor: pointer;
  }
`;

const Container = styled.div`
  min-width: 100%;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
`;

const Save = styled.button`
  padding: 5px;
  margin: 10px;

  &:hover {
    cursor: pointer;
  }
`;

const BasicBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

//重新整理後，必帶最近一次舊資料
//抓資料：先從 CMS 調用，沒資料再看 local-storage 有沒有資料
//存資料：CMS 跟 local-storage 都存

export default function PlayPage() {
  studio.initialize();
  const config = {}; // the config can be empty when starting a new project
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.hide();

  const [boxes, setBoxes] = useState([]); //紀錄新增的資料
  const [preData, setPreData] = useState([]); //上一筆舊資料，要與 local-storage 一致

  //從 local-storage 取得舊資料
  useEffect(() => {
    const storedBoxes = localStorage.getItem('theatre-data');
    if (storedBoxes) {
      setPreData(JSON.parse(storedBoxes));
    }
  }, []);

  console.log('boxes', boxes);
  console.log('preData', preData);

  //render 出既有的元件
  useEffect(() => {
    preData
      .filter((item) => item.type === 'box')
      .forEach((item) => {
        const box = sheet.object(item.id, {
          position: {
            x: 0,
            y: 0,
          },
          bgColor: types.string('#ffffff'),
        });

        const element = document.getElementById(item.id);

        console.log('element', element);

        box.onValuesChange((newValue) => {
          element.style.left = newValue.position.x + 'px';
          element.style.top = newValue.position.y + 'px';
          element.style.backgroundColor = newValue.bgColor;
        });
      });
  });

  const addBox = (name = '', content = '') => {
    //新增一個控制項
    const box = sheet.object(`${name}`, {
      position: {
        x: 0,
        y: 0,
      },
      bgColor: types.string('#ffffff'),
    });

    const div = document.createElement('div');

    div.style.cssText = `
    position: absolute;
    width: 300px;
    height: 20px;
    background: white;
    outline: 3px solid black;
    z-index: 10000;
    padding: 20px;
  `;

    div.textContent = `${content}`;
    div.id = `${name}`;

    const container = document.getElementById('item-container');

    if (container) {
      container.appendChild(div);
    }

    let style = {};

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.fontSize = newValue.size;
      div.style.color = newValue.color;
    });

    setBoxes((prevBoxes) => [
      ...prevBoxes,
      { content, element: div, id: name, type: 'box', style: style },
    ]);
  };

  // const addFont = (name = '', content = '') => {
  //   const box = sheet.object(`${name}`, {
  //     position: {
  //       x: 0,
  //       y: 0,
  //     },
  //     size: types.string('50px'),
  //     color: types.string('#000000'),
  //   });

  //   // const div = document.createElement('div');

  //   // div.style.cssText = `
  //   //   font-size: 50px;
  //   //   position: absolute;
  //   //   color: black;
  //   //   z-index: 10000000;
  //   // `;

  //   div.textContent = `${content}`;

  //   document.body.appendChild(div);

  //   box.onValuesChange((newValue) => {
  //     div.style.left = newValue.position.x + 'px';
  //     div.style.top = newValue.position.y + 'px';
  //     div.style.fontSize = newValue.size;
  //     div.style.color = newValue.color;
  //   });
  // };

  // const addEmbedded = (embed, setEmbed) => {
  //   const embedded = sheet.object('embedded', {
  //     position: {
  //       x: 0,
  //       y: 0,
  //     },

  //     scale: types.number(1, {
  //       range: [0, 5],
  //       nudgeMultiplier: 0.1,
  //     }),
  //   });

  //   const div = document.createElement('div');

  //   div.style.cssText = `
  //     position: absolute;
  //     width: 100%;
  //     height: 100vh;
  //     background: white;
  //     transition: transform 0.3s ease;
  //     transform-origin: top left;
  //   `;

  //   document.body.appendChild(div);

  //   embedded.onValuesChange((newValue) => {
  //     div.style.left = newValue.position.x + 'px';
  //     div.style.top = newValue.position.y + 'px';
  //     div.style.transform = `scale(${newValue.scale})`;
  //   });

  //   ReactDOM.render(<EmbeddedCode embed={embed} setEmbed={setEmbed} />, div);
  // };

  const [showEmBox, setShowEmBox] = useState(false);
  const [showFontBox, setShowFontBox] = useState(false);
  const [showFont, setShowFont] = useState(false);
  const [embed, setEmbed] = useState('');
  const [font, setFont] = useState('');
  const [pureFont, setPureFont] = useState('');
  const [boxName, setBoxName] = useState('box');
  const [pureFontName, setPureFontName] = useState('font');

  //這邊處理轉換 embedded-code
  // const embedCodeWebpackAssets = embedCodeGen.loadWebpackAssets(); //取得靜態資料

  return (
    <>
      {showEmBox && (
        <EmbeddedBox
          setShowEmBox={setShowEmBox}
          setEmbed={setEmbed}
          embed={embed}
          addEmbedded={addEmbedded}
        />
      )}

      {showFontBox && (
        <FontBox
          setShowFontBox={setShowFontBox}
          setFont={setFont}
          font={font}
          setBoxName={setBoxName}
          boxName={boxName}
          addBox={addBox}
        />
      )}

      {showFont && (
        <Font
          setShowFont={setShowFont}
          setPureFont={setPureFont}
          pureFont={pureFont}
          setPureFontName={setPureFontName}
          pureFontName={pureFontName}
          addFont={addFont}
        />
      )}

      <BasicWrapper>
        <Save
          onClick={() => {
            console.log('有存進 local-storage ');
            const mergedData = [...preData, ...boxes]; // 合并 preData 和 boxes 数组
            localStorage.setItem('theatre-data', JSON.stringify(mergedData));
          }}
        >
          儲存
        </Save>
        <Save
          onClick={() => {
            console.log(`清空 theatre-data 的 local storage`);
            localStorage.removeItem('theatre-data');
          }}
        >
          清空紀錄
        </Save>
        <Play
          onClick={() => {
            sheet.sequence.play({
              iterationCount: Infinity,
            });
          }}
        >
          播放動畫
        </Play>
        <Play
          onClick={() => {
            sheet.sequence.pause();
          }}
        >
          暫停
        </Play>

        <Play
          onClick={() => {
            studio.ui.hide();
          }}
        >
          隱藏介面
        </Play>
        <Play
          onClick={() => {
            studio.ui.restore();
          }}
        >
          開啟介面
        </Play>
      </BasicWrapper>

      <AddWrapper>
        <AddButton
          className='add-embedded-box'
          onClick={() => {
            setShowEmBox(true);
          }}
        >
          新增專案（project）
        </AddButton>

        <AddButton
          className='add-embedded-box'
          onClick={() => {
            setShowEmBox(true);
          }}
        >
          新增場景（Scene）
        </AddButton>

        <AddButton
          className='add-embedded-box'
          onClick={() => {
            setShowEmBox(true);
          }}
        >
          新增embedded
        </AddButton>
        <AddButton
          className='add-font-box'
          onClick={() => {
            setShowFontBox(true);
          }}
        >
          新增文字框
        </AddButton>
        <AddButton
          className='add-font'
          onClick={() => {
            setShowFont(true);
          }}
        >
          新增文字標題
        </AddButton>

        <AddButton className='delete-element'>刪除元件</AddButton>
      </AddWrapper>

      {/* 只 render local-storage 的舊資料 */}
      <Container id='item-container'>
        {preData.map((box, index) => (
          <BasicBox key={index} id={box.id} style={box.style}>
            {box.content}
          </BasicBox>
        ))}
      </Container>
    </>
  );
}
