import React from 'react';
import studio from '@theatre/studio';
import styled from 'styled-components';

const BasicWrapper = styled.div`
  position: fixed;
  padding: 10px;
  bottom: 0;
  left: 0;
  background: #f4dac8;
  max-width: 100px;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: white;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'save data',
    onClick: ({ data }) => {
      // const mergedData = [...preData, ...boxes]; // 合併 preData 和 boxes 数组
      localStorage.setItem('theatre-data', JSON.stringify(data));
      alert('資料已存進 local-storage');
    },
  },
  {
    label: 'clear data',
    onClick: () => {
      localStorage.removeItem('theatre-data');
      alert('已將 local-storage 資料清空');
    },
  },
  {
    label: 'play',
    onClick: ({ sheet }) => {
      sheet.sequence.play({
        iterationCount: Infinity,
      });
    },
  },
  {
    label: 'pause',
    onClick: ({ sheet }) => {
      sheet.sequence.pause();
    },
  },
  {
    label: '隱藏介面',
    onClick: () => {
      studio.ui.hide();
    },
  },
  {
    label: '開啟介面',
    onClick: () => {
      studio.ui.restore();
    },
  },
];

export default function BasicButtons({ sheet, data }) {
  return (
    <BasicWrapper>
      {buttons.map((button, index) => (
        <button key={index} onClick={() => button.onClick({ sheet, data })}>
          {button.label}
        </button>
      ))}
    </BasicWrapper>
  );
}
