import React from 'react';
import styled from 'styled-components';
// import { initialConfig } from '../constants';
// import studio from '@theatre/studio';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export default function Stage({ savedData, sheet }) {
  const fontElements = savedData
    .filter((data) => data.type === 'font')
    .map((data, index) => {
      return (
        <FontElement
          key={index}
          id={data.id}
          // onClick={() => {
          //   const obj = sheet.object(data.id, initialConfig.FONT);
          //   studio.setSelection([obj]);
          // }}
        >
          {data.content}
        </FontElement>
      );
    });

  return <Wrapper id='theatre-stage'>{fontElements}</Wrapper>;
}
