  setObjData,
  setAnimateJSON,
}) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          onClick={() =>
            button.onClick({
              setShowJSONLightBox,
            })
          }
        >
          {button.label}
        </button>
      ))}

      <Link to='/demo-scroll' target='_blank'>
        <button>Scroll</button>
      </Link>

      <Link to='/demo-video' target='_blank'>
        <button>Video</button>
      </Link>
    </Wrapper>
  );
}
