import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 50vh;
  position: relative;
  z-index: -1;
  outline: 10px solid red;
  background: grey;
`;

const FontElement = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 1000;
  padding: 20px;
`;

// element 元件都先寫在這邊

export default function Stage({ previousData }) {
  return (
    <Wrapper id='theatre-stage'>
      <div>測試測試</div>
      {previousData.map((box, index) => (
        <FontElement key={index} id={box.id} style={box.style}>
          {box.content}
        </FontElement>
      ))}
    </Wrapper>
  );
}
