import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  /* width: 0%; */
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;
  textarea {
    width: 100%;
    height: 100px;
    margin-bottom: 10px;
  }

  button {
    padding: 10px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

const Container = styled.div`
  display: flex;
  /* justify-content: space-between; */
`;

const Block = styled.div`
  margin: 0 30px;
  width: 400px;
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  setAnimateJSON,
  animateJSON,
  elementJSON,
  setElementJSON,
}) {
  // storage 資料是 JSON --> 為了 render 轉成 object --> 呈現改回 JSON
  // 現在希望

  console.log('animateJSON', animateJSON);

  return (
    <Wrapper>
      <Form>
        <h2>JSON 資料</h2>

        <Container>
          <Block>
            <p>元件 JSON</p>
            <textarea
              placeholder='元件JSON'
              value={elementJSON}
              onChange={(e) => setElementJSON(e.target.value)}
            />

            <p>動畫 JSON (read only)</p>

            {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
            <textarea
              placeholder='動畫JSON'
              value={JSON.stringify(animateJSON)}
              onChange={(e) => setAnimateJSON(e.target.value)}
            />

            <button
              onClick={() => {
                setShowJSONLightBox(false);
              }}
            >
              close
            </button>
          </Block>

          <Block>
            <p>元件 JSON</p>
            <textarea
              placeholder='元件JSON'
              value={elementJSON}
              onChange={(e) => setElementJSON(e.target.value)}
            />

            <p>動畫 JSON (read only)</p>

            {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
            <textarea
              placeholder='動畫JSON'
              value={JSON.stringify(animateJSON)}
              onChange={(e) => setAnimateJSON(e.target.value)}
            />

            <button
              onClick={() => {
                setShowJSONLightBox(false);
              }}
            >
              close
            </button>
          </Block>
        </Container>

        {/* <textarea
          placeholder='動畫JSON'
          value={JSON.stringify(parsedAnimateJSON, null, 2)} // Pretty print JSON for display
          onChange={(e) => setAnimateJSON(e.target.value)} // Update the JSON directly as an object
        /> */}

        {/* <button
          onClick={() => {
            setShowJSONLightBox(false);
            localStorage.setItem('theatre-data', elementJSON);

            const oldAnimateData = localStorage.getItem(
              'theatre-0.4.persistent'
            );

            const updatedAnimateData = {
              ...oldAnimateData,
              historic: {
                innerState: {
                  coreByProject: {
                    Project: animateJSON,
                  },
                },
              },
            };

            localStorage.setItem('theatre-0.4.persistent', updatedAnimateData);

            window.location.reload();
          }}
        >
          儲存修改
        </button> */}
      </Form>
    </Wrapper>
  );
}
