import { css } from 'styled-components';

const defaultFontStyle = css`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  word-wrap: break-word;
  transform-origin: center;
`;

const defaultImageStyle = css`
  position: absolute;
  display: block;
  overflow: hidden;
  height: auto;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    outline: 1px solid blue;
    display: block;
  }
`;

const defaultBgStyle = css`
  position: absolute;
  display: block;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  width: 100%;
  height: 100vh;
  z-index: -1;
`;

export { defaultFontStyle, defaultImageStyle, defaultBgStyle };
