import studio from '@theatre/studio';
import { getProject } from '@theatre/core';
import Stage from '../components/stage';
import Dimmer from '../components/dimmer-with-message';
import { useState, useEffect, useRef } from 'react';

export default function DemoVideo() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');

  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: Infinity,
    })
  );

  const objJson = localStorage.getItem('theatre-data'); // get objectJSON in local-storage
  const objData = objJson ? JSON.parse(objJson) : [];

  const [loadedImages, setLoadedImages] = useState(0); // image & background onload
  const [isLoading, setIsLoading] = useState(true);

  const totalImages = objData.filter(
    (data) => data.type === 'IMAGE' || data.type === 'BACKGROUND'
  ).length;

  useEffect(() => {
    if (loadedImages === totalImages) {
      setIsLoading(false);
    }
  }, [loadedImages, totalImages]);

  const [hasImgError, setHasImgError] = useState(false); // image & background onError

  return (
    <>
      <Dimmer
        show={isLoading && !hasImgError}
        message={'載入中'}
        shining={true}
      />
      <Dimmer
        show={hasImgError}
        message={'載入失敗。請檢查您的網路連線，並重新整理瀏覽器。'}
      />
      <Stage
        objData={objData}
        sheet={sheet}
        stageWidth={window.innerWidth}
        draggable={false}
        setHasImgError={setHasImgError}
        setLoadedImages={setLoadedImages}
      />
    </>
  );
}
