import { types } from '@theatre/core';
import { useState, useEffect } from 'react';
import { initialConfig } from '../constants';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ImageElement = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
`;

export default function DemoVideo({ project, sheet }) {
  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: Infinity,
    })
  );

  const [storedData, setStoredData] = useState([]); // data in local-storage

  useEffect(() => {
    const elements = localStorage.getItem('theatre-data');
    if (elements) {
      setStoredData(JSON.parse(elements));
    }
  }, []);

  // 如何修改內容？？
  useEffect(() => {
    if (storedData.length > 0) {
      //render `font` control menu
      storedData
        .filter((item) => item.type === 'font')
        .forEach((item) => {
          const menuConfig = sheet.object(item.id, {
            ...initialConfig.FONT,
            content: types.string(`${item.content}`),
          });

          const element = document.getElementById(item.id);

          menuConfig.onValuesChange((newValue) => {
            element.style.cssText = `
              left: ${newValue.position.x}%;
              top: ${newValue.position.y}修改ㄋㄨ
              background: ${newValue.bgColor};
              color: ${newValue.font.color};
              opacity: ${newValue.opacity};
              padding: ${newValue.padding}px;
              display: ${newValue.visible ? 'block' : 'none'};
              width: ${newValue.size.width}px;
              height: ${newValue.size.height}px;
              font-size: ${newValue.font.size}px;
              border-radius: ${newValue.border.radius}%;
              border: ${newValue.border.size}px solid ${newValue.border.color};
            `;
          });
        });

      //render `image` control menu
      storedData
        .filter((item) => item.type === 'image')
        .forEach((item) => {
          const menuConfig = sheet.object(item.id, {
            ...initialConfig.IMAGE,
            // content: types.string(`${item.content}`),
          });

          const element = document.getElementById(item.id);

          menuConfig.onValuesChange((newValue) => {
            element.style.cssText = `
              left: ${newValue.position.x}%;
              top: ${newValue.position.y}%;
              background: ${newValue.bgColor};
              color: ${newValue.font.color};
              opacity: ${newValue.opacity};
              padding: ${newValue.padding}px;
              display: ${newValue.visible ? 'block' : 'none'};
              width: ${newValue.size.width}px;
              height: ${newValue.size.height}px;
              font-size: ${newValue.font.size}px;
              border-radius: ${newValue.border.radius}%;
              border: ${newValue.border.size}px solid ${newValue.border.color};
            `;
          });
        });
    }
  }, [storedData]);

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
    </Wrapper>
  );
}
