import React from 'react';
import studio from '@theatre/studio';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Wrapper = styled.div`
  padding: 10px;
  background: #f4dac8;
  max-width: 180px;
  margin-right: 15px;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: white;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'save data',
    onClick: ({ elementData }) => {
      localStorage.setItem('theatre-data', JSON.stringify(elementData));
      alert('資料已儲存(localStorage)');
    },
  },
  {
    label: 'clear data',
    onClick: () => {
      localStorage.removeItem('theatre-data');
      localStorage.removeItem('theatre-0.4.persistent');
      alert('資料已清空(localStorage)');
      window.location.reload();
    },
  },
  {
    label: 'play',
    onClick: ({ sheet }) => {
      sheet.sequence.play({
        iterationCount: Infinity,
      });
    },
  },
  {
    label: 'pause',
    onClick: ({ sheet }) => {
      sheet.sequence.pause();
    },
  },
  {
    label: '隱藏介面',
    onClick: () => {
      studio.ui.hide();
    },
  },
  {
    label: '開啟介面',
    onClick: () => {
      studio.ui.restore();
    },
  },

  {
    label: 'show JSON data',
    onClick: ({ setShowJSONLightBox }) => {
      setShowJSONLightBox(true);
    },
  },

  {
    label: 'show Embedded data',
    onClick: () => {
      alert('show Embedded data');
    },
  },
];

export default function SettingButtons({
  sheet,
  elementData,
  setShowJSONLightBox,
}) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          onClick={() =>
            button.onClick({ sheet, elementData, setShowJSONLightBox })
          }
        >
          {button.label}
        </button>
      ))}

      <Link to='/demo-scroll' target='_blank'>
        <button>預覽 Scroll 效果</button>
      </Link>

      <Link to='/demo-video' target='_blank'>
        <button>預覽 Video 效果</button>
      </Link>
    </Wrapper>
  );
}
