import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function DeleteLightBox({
  setShowDeleteBox,
  setStoredData,
  storedData,
}) {
  return (
    <Wrapper>
      <Form>
        <h2>刪除元件 </h2>

        <span>刪除 id </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button onClick={() => {}}>確定</button>

        <button
          onClick={() => {
            setShowDeleteBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
