import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import projectState from '../json/state.json';
import LightBox from '../components/lightbox';
import Buttons from '../components/button';

const Wrapper = styled.div`
  min-width: 100%;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
`;

//重新整理後，必帶最近一次舊資料
//抓資料：先從 CMS 調用，沒資料再看 local-storage 有沒有資料
//存資料：CMS 跟 local-storage 都存

export default function Stage({ previousData }) {
  return (
    <Wrapper id='theatre-stage'>
      {previousData.map((box, index) => (
        <FontElement key={index} id={box.id} style={box.style}>
          {box.content}
        </FontElement>
      ))}
    </Wrapper>
  );
}
