import React from 'react';
import styled from 'styled-components';

const RangeBar = styled.div`
  position: fixed;
  left: 30px;
  bottom: 220px;
  width: 180px;
  height: 20px;
  z-index: 100;

  //可以移動的 range
  input {
    width: 100%;
    position: absolute;
    top: 50%;
    z-index: 3;
    transform: translateY(-50%);
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    height: 4px;
    opacity: 0;
    margin: 0;
    outline: 3px solid blue;
  }

  /* .range-slider_thumb {
    width: 45px;
    height: 45px;
    border: 0.6px solid #303030;
    position: absolute;
    top: 50%;
    transform: translateY(-50%) translateX(-50%);
    background-color: #f4f4f4;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: 700;
    font-size: 3px;
    color: #303030;
    z-index: 2;
  } */

  /* .range-slider_line {
    height: 4px;
    width: 100%;
    background-color: #e1e1e1;
    top: 50%;
    transform: translateY(-50%);
    left: 0;
    position: absolute;
    z-index: 1;
  } */

  .range-slider_line-fill {
    position: absolute;
    height: 4px;
    background-color: #303030;
  }
`;

const Thumb = styled.div`
  width: 45px;
  height: 45px;
  border: 0.6px solid #303030;
  position: absolute;
  top: 50%;
  transform: translateY(-50%) translateX(-50%);
  background-color: #f4f4f4;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 700;
  font-size: 3px;
  color: #303030;
  z-index: 2;
  border-radius: 50%;
  cursor: pointer;
`;

const Line = styled.div`
  height: 4px;
  width: 100%;
  background-color: #e1e1e1;
  top: 50%;
  transform: translateY(-50%);
  left: 0;
  position: absolute;
  z-index: 1;
`;

export default function RangeSlider({ sliderValue, setSliderValue }) {
  const screenWidth = window.innerWidth;

  function handleChange(event) {
    const value = parseInt(event.target.value, 10);
    setSliderValue(value);
  }

  return (
    <RangeBar>
      <Thumb style={{ left: (sliderValue / screenWidth) * 200 + 'px' }}>
        {sliderValue}px
      </Thumb>
      <Line>
        <div
          className='range-slider_line-fill'
          style={{ width: (sliderValue / screenWidth) * 100 + '%' }}
        ></div>
      </Line>
      <input
        className='range-slider_input'
        type='range'
        value={sliderValue}
        min='0'
        max={screenWidth}
        onChange={handleChange}
      />
    </RangeBar>
  );
}
