import studio from '@theatre/studio';
import React, { useMemo, useState, useEffect } from 'react';
import styled from 'styled-components';
import useDrag from '../../hooks/use-drag';
import { initialConfig } from '../../constants';
import { defaultBgStyle } from '../../shared-style';

const BgWrapper = styled.div`
  ${defaultBgStyle}
`;

export default function BackgroundElement({ id, sheet, screenWidth }) {
  const object = sheet.object(id, {
    ...initialConfig.BACKGROUND,
  });

  // Drag setting ----------------------

  const [divRef, setDivRef] = useState(null);
  const screenHeight = window.innerHeight;

  const dragOpts = useMemo(() => {
    let scrub;
    let initial;
    let firstOnDragCalled = false;
    return {
      onDragStart() {
        scrub = studio.scrub();
        initial = object.value;
        firstOnDragCalled = false;
      },

      onDrag(x, y) {
        const percentX = (x / screenWidth) * 100;
        const percentY = (y / screenHeight) * 100;

        if (!firstOnDragCalled) {
          studio.setSelection([object]);
          firstOnDragCalled = true;
        }
        scrub.capture(({ set }) => {
          set(object.props, {
            ...initial,
            position: {
              x: percentX + initial.position.x,
              y: percentY + initial.position.y,
            },
          });
        });
      },
      onDragEnd(dragHappened) {
        if (dragHappened) {
          scrub.commit();
        } else {
          scrub.discard();
        }
      },
      lockCursorTo: 'move',
    };
  }, []);

  useDrag(divRef, dragOpts);

  // Style setting ----------------------

  const [style, setStyle] = useState({});

  useEffect(() => {
    object.onValuesChange((newValue) => {
      setStyle({
        left: `${newValue.position.x}%`,
        top: `${newValue.position.y}%`,
        width: `${newValue.size.width}px`,
        borderRadius: `${newValue.border.radius}%`,
        border: `${newValue.border.size}px solid ${newValue.border.color}`,
        background: `${newValue.bgColor}`,
        opacity: `${newValue.opacity}`,
        display: `${newValue.visible ? 'block' : 'none'}`,
        backgroundImage: `url(${newValue.url})`,
        transform: `scale(${newValue.scale})`,
        zIndex: `${newValue.zIndex}`,
      });
    });
  }, [object]);

  return (
    <BgWrapper
      onClick={() => {
        studio.setSelection([object]);
      }}
      ref={setDivRef}
      style={style}
    >
      <img src='/default-image.png' alt={id} />
    </BgWrapper>
  );
}
