import React from 'react';
import studio from '@theatre/studio';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const ButtonWrapper = styled.div`
  padding: 10px;
  background: #f4dac8;
  max-width: 100px;
  margin: 0 10px 0 0;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: white;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'save data',
    onClick: ({ data }) => {
      localStorage.setItem('theatre-data', JSON.stringify(data));
      alert('資料已存進 local-storage');
    },
  },
  {
    label: 'clear data',
    onClick: () => {
      localStorage.removeItem('theatre-data');
      alert('已將 local-storage 資料清空');
      window.location.reload();
    },
  },
  {
    label: 'play',
    onClick: ({ sheet }) => {
      sheet.sequence.play({
        iterationCount: Infinity,
      });
    },
  },
  {
    label: 'pause',
    onClick: ({ sheet }) => {
      sheet.sequence.pause();
    },
  },
  {
    label: '隱藏介面',
    onClick: () => {
      studio.ui.hide();
    },
  },
  {
    label: '開啟介面',
    onClick: () => {
      studio.ui.restore();
    },
  },
  {
    label: '預覽效果',
    onClick: () => {
      studio.ui.restore();
    },
  },
];

export default function SettingButtons({ sheet, data }) {
  return (
    <ButtonWrapper>
      {buttons.map((button, index) => (
        <button key={index} onClick={() => button.onClick({ sheet, data })}>
          {button.label}
        </button>
      ))}

      <Link to='/demo' target='_blank'>
        <button>預覽 DemoScroll 效果</button>
      </Link>
      <Link to='/demo' target='_blank'>
        <button>預覽 DemoScroll 效果</button>
      </Link>
    </ButtonWrapper>
  );
}
