import styled from 'styled-components';

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 0.9;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10000;
`;

export default function FontLightBox({
  setShowFontBox,
  setFont,
  font,
  boxName,
  setBoxName,
  addBox,
}) {
  return (
    <Wrapper>
      <Form>
        <div>
          <h2>新增 Font 元件 </h2>

          <span>物件ID (勿與之前的重複）：</span>
          <input
            placeholder='中/英/數字皆可'
            value={boxName}
            onChange={(e) => setBoxName(e.target.value)}
          />

          <textarea
            placeholder='文字內容'
            value={font}
            onChange={(e) => setFont(e.target.value)}
          />

          <button
            onClick={() => {
              setShowFontBox(false);
              addBox(boxName, font);
            }}
          >
            確定
          </button>

          <button
            onClick={() => {
              setShowFontBox(false);
            }}
          >
            取消
          </button>
        </div>
      </Form>
    </Wrapper>
  );
}
