import React from 'react';
import styled from 'styled-components';
import { defaultFontStyle, defaultImageStyle } from '../shared-style/index';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle}
  background-image: url(${(props) => props.url});
`;

export default function Stage({ storedData, ref }) {
  const fontElements = storedData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return (
        <FontElement key={index} id={data.id}>
          {data.content}
        </FontElement>
      );
    });

  const imageElements = storedData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      return <ImageElement key={index} id={data.id} url={data.url} />;
    });

  return (
    <Wrapper id='theatre-stage' ref={ref}>
      {fontElements}
      {imageElements}
    </Wrapper>
  );
}
