import { types } from '@theatre/core';

export const FONT = {
  position: types.compound({
    x: types.number(40, {
      range: [0, 100],
      nudgeMultiplier: 0.5,
    }),
    y: types.number(40, {
      range: [0, 100],
      nudgeMultiplier: 0.5,
    }),
  }),
  size: types.compound({
    width: types.number(200, {
      range: [0, 1200],
      nudgeMultiplier: 1,
    }),
    // height: types.number(100, {
    //   range: [0, 1200],
    //   nudgeMultiplier: 1,
    // }),
  }),
  bgColor: types.rgba({ r: 255, g: 255, b: 255, a: 1 }),
  font: types.compound({
    size: types.number(12, { range: [0, 300], nudgeMultiplier: 1 }),
    color: types.rgba({ r: 0, g: 0, b: 0, a: 1 }),
  }),
  border: types.compound({
    size: types.number(1),
    color: types.rgba({ r: 0, g: 0, b: 0, a: 1 }),
    radius: types.number(0, {
      range: [0, 100],
      nudgeMultiplier: 1,
    }),
  }),
  title: types.string(''),
  content: types.string(''),
  opacity: types.number(1, {
    range: [0, 1],
    nudgeMultiplier: 0.05,
  }),
  padding: types.number(5, {
    range: [0, 100],
    nudgeMultiplier: 1,
  }),
  visible: types.boolean(true),
  zIndex: types.stringLiteral('middle', {
    0: 'bottom(0)',
    10: 'lower(10)',
    100: 'middle(100)',
    1000: 'upper(1000)',
    10000: 'top(10000)',
  }),
};
