import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { initialProperties } from '../../constants';
import studio from '@theatre/studio';
import { types } from '@theatre/core';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10005;
`;

const Form = styled.div`
  width: 50%;
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;

  textarea {
    width: 100%;
    height: 200px;
    margin-bottom: 10px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  sheet,
  setPreviousData,
  previousData,
  setAnimateJSON,
  animateJSON,
}) {
  // 1. 更新得到的 positionJSON 給 previousData
  // 2. 更新得到的 animateJSON 直接讓 project 讀取
  // 3. 如果都沒資料，就使用預設值 --> 空白頁面

  const elements = localStorage.getItem('theatre-data');

  //get local-storage previous data
  useEffect(() => {
    // 沒有 previousData 的話，跟 local-storage 要
    if (previousData.length !== 0) {
      const elements = localStorage.getItem('theatre-data');
      if (elements) {
        setPreviousData(JSON.parse(elements));
      }
    }
  }, []);

  return (
    <Wrapper>
      <Form>
        <h2>新增 JSON 資料</h2>

        <textarea
          placeholder='元件JSON'
          value={previousData}
          onChange={(e) => setPreviousData(e.target.value)}
        />

        <textarea
          placeholder='動態JSON'
          value={animateJSON}
          onChange={(e) => setAnimateJSON(e.target.value)}
        />

        <button
          onClick={() => {
            setShowJSONLightBox(false);
            // addFontElement(fontID, fontContent);
          }}
        >
          確認
        </button>

        <button
          onClick={() => {
            setShowJSONLightBox(false);

            const elements = localStorage.getItem('theatre-data');
            if (elements) {
              setPreviousData(JSON.parse(elements));
            }
          }}
        >
          skip，use default project
        </button>
      </Form>
    </Wrapper>
  );
}
