import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  /* width: 30%; */
  /* height: 30%; */
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;
  padding: 50px;

  input {
    width: 50%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({
  setShowFontLightBox,
  setStoredData,
  storedData,
}) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  // 改成都只存空的？內容都在 animateJSON 記錄？
  const addFontElement = (id = '') => {
    const updatedElementData = [
      ...storedData,
      // { id: id, content: content, type: 'FONT' },
      { id: id, type: 'FONT' },
    ];

    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
    setStoredData(updatedElementData);
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        {/* <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        /> */}

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
