import React from 'react';
import styled from 'styled-components';

const gridColor = '#29e2f3';
const dashWidth = '2px';
const dashSpacing = '10px';
const redColor = 'red';

const Grid = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  margin: auto;

  background-image: repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    );
  background-size: 100px 100px;
  background-repeat: repeat;
  background-position: 100% 100%;
  pointer-events: none;
  opacity: 0.5;
`;

const HorizonLine = styled.hr`
  position: absolute;
  top: 50%;
  width: 100%;
  height: 2px;
  background-image: linear-gradient(
    90deg,
    red,
    red 75%,
    transparent 75%,
    transparent 100%
  );
  background-size: 20px 1px;
  border: none;
  transform: translateY(-50%);
`;

const VerticalLine = styled.hr`
  position: absolute;
  left: 50%;
  width: 2px;
  height: 100%;
  background-image: linear-gradient(
    90deg,
    red,
    red 75%,
    transparent 75%,
    transparent 100%
  );
  background-size: 1px 20px; /* Adjust the background-size for vertical orientation */
  border: none;
  transform: translateX(-50%); /* Center the element horizontally */
`;

export default function GridLine() {
  return (
    <>
      <Grid />
      <HorizonLine />
      <VerticalLine />
    </>
  );
}
