import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';
import VideoElement from './elements/video';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

export default function Stage({
  objData,
  sheet,
  stageWidth,
  draggable,
  setLoadedImages = () => {},
  setHasImgError = () => {},
}) {
  const handleOnLoad = () => {
    setLoadedImages((prev) => prev + 1);
  };

  const handleOnError = () => {
    setHasImgError(true);
  };

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleOnLoad}
          onError={handleOnError}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleOnLoad}
          onError={handleOnError}
        />
      );
    });

  const videoElements = objData
    .filter((data) => data.type === 'VIDEO')
    .map((data) => {
      return (
        <VideoElement
          key={'video' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
      {bgElements}
      {/* {videoElements} */}

      <video controls autoPlay>
        <source
          src='https://storage.cloud.google.com/v3-statics-staging.mirrormedia.mg/files/20161130141317-3863a0e1b9a81cc40dee4f281fc70274.mp4'
          type='video/mp4'
        />
      </video>
    </Wrapper>
  );
}
