import React from 'react';
import styled from 'styled-components';

const LargeGrid = styled.div`
  /* position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-image: linear-gradient(to right, transparent 99.5px, black 0.5px),
    linear-gradient(to bottom, transparent 99.5px, black 0.5px);
  background-size: 100px 100px;
  background-repeat: repeat; */

  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  /* background-image: linear-gradient(
      to right,
      transparent 49px,
      red 1px,
      transparent 49px
    ),
    linear-gradient(to bottom, transparent 49px, red 1px, transparent 49px);
  background-size: 100px 100px;
  background-repeat: repeat; */

  background-image: repeating-linear-gradient(
      to right,
      red 0,
      red 1px,
      transparent 1px,
      transparent 49px
    ),
    repeating-linear-gradient(
      to right,
      grey 0,
      grey 1px,
      transparent 1px,
      transparent 99px
    ),
    repeating-linear-gradient(
      to bottom,
      red 0,
      red 1px,
      transparent 1px,
      transparent 49px
    ),
    repeating-linear-gradient(
      to bottom,
      black 0,
      black 1px,
      transparent 1px,
      transparent 99px
    );
  background-size: 100px 100px;
  background-repeat: repeat;
`;

const SmallGrid = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;

  background-image: linear-gradient(to right, transparent 49.5px, red 0.5px),
    linear-gradient(to bottom, transparent 49.5px, red 0.5px);
  background-size: 50px 50px;

  background-repeat: repeat;
`;

export default function GuideLine() {
  return (
    <>
      <LargeGrid />
      <SmallGrid />
    </>
  );
}
