import React, { useState } from 'react';
import styled from 'styled-components';
import { initialConfig } from '../../constants';
import studio from '@theatre/studio';
import { types } from '@theatre/core';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10005;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({
  setShowFontLightBox,
  setElementData,
  sheet,
}) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  const addFontElement = (fontID = '', fontContent = '') => {
    const menuConfig = sheet.object(`${fontID}`, {
      ...initialConfig.FONT,
      content: types.string(`${fontContent}`),
    });

    console.log('有執行addFontElement');

    const div = document.createElement('div');

    div.id = `${fontID}`;
    div.textContent = `${fontContent}`;
    div.onclick = () => studio.setSelection([menuConfig]);
    div.style.cssText = `
      position: absolute;
      z-index: 1000;
      display: flex;
      align-items: center;
      justify-content: center'
      cursor: pointer;
    `;

    const container = document.getElementById('theatre-stage');
    if (container) {
      container.appendChild(div);
    }

    let updateStyle;

    menuConfig.onValuesChange((newValue) => {
      console.log('newValue', newValue);
      div.style.left = newValue.position.x + '%';
      div.style.top = newValue.position.y + '%';
      div.style.background = newValue.bgColor;
      div.style.color = newValue.font.color;
      div.style.opacity = newValue.opacity;
      div.style.padding = newValue.padding + 'px';
      div.style.display = newValue.visible ? 'block' : 'none';
      div.style.width = newValue.size.width + 'px';
      div.style.height = newValue.size.height + 'px';
      div.style.fontSize = newValue.font.size + 'px';
      div.style.borderRadius = newValue.border.radius + '%';
      div.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;

      updateStyle = {
        position: 'absolute',
        zIndex: '1000',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer',
        left: newValue.position.x + '%',
        top: newValue.position.y + '%',
        color: newValue.font.color,
        fontSize: newValue.font.size + 'px',
        background: newValue.bgColor,
        padding: newValue.padding + 'px',
        display: newValue.visible ? 'block' : 'none',
        width: newValue.size.width + 'px',
        height: newValue.size.height + 'px',
        opacity: newValue.opacity,
        borderRadius: newValue.border.radius + '%',
        border: newValue.border.size + 'px solid' + newValue.border.color,
      };

      div.textContent = newValue.content;
    });

    // setElementData((prev) => [
    //   ...prev,
    //   {
    //     content: fontContent,
    //     id: fontID,
    //     type: 'font',
    //     style: updateStyle,
    //   },
    // ]);
  };

  // 方法一：當按儲存時，把新的元件資料跟 element-data 合併，存進 LS

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID, fontContent);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
