import styled from 'styled-components';
import useDrag from '../../hooks/use-drag';
import studio from '@theatre/studio';
import { initialProperties } from '../../constants';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  background: grey;
`;

const FontElement = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 1000;
  padding: 20px;
  cursor: pointer;
`;

export default function Stage({ previousData, sheet }) {
  return (
    <Wrapper id='theatre-stage'>
      {previousData.map((data, index) => {
        return (
          <FontElement
            key={index}
            id={data.id}
            style={data.style}
            onClick={() => {
              const obj = sheet.object(data.id, initialProperties.FONT);
              studio.setSelection([obj]);
            }}
          >
            {data.content}
          </FontElement>
        );
      })}
    </Wrapper>
  );
}
