import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  z-index: -1;
  background: grey;
`;

const FontElement = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 1000;
  padding: 20px;
`;

// element 元件都先寫在這邊

function parseStyles(styleString) {
  const styles = {};
  const declarations = styleString.split(';');

  for (let declaration of declarations) {
    const [property, value] = declaration.split(':');
    if (property && value) {
      styles[property.trim()] = value.trim();
    }
  }

  return styles;
}

export default function Stage({ previousData }) {
  return (
    <Wrapper id='theatre-stage'>
      {previousData.map((box, index) => {
        const fontStyle = parseStyles(box.style);
        return (
          <FontElement key={index} id={box.id} style={fontStyle}>
            {box.content}
          </FontElement>
        );
      })}
    </Wrapper>
  );
}
