import { types } from '@theatre/core';

export const initialProperties = {
  FONT: {
    position: types.compound({
      x: types.number(0),
      y: types.number(0),
    }),
    size: types.compound({
      width: types.number(0, {
        range: [0, 1200],
      }),
      height: types.number(0, {
        range: [0, 1200],
      }),
    }),
    color: types.compound({
      bg: types.rgba({ r: 255, g: 255, b: 255, a: 1 }),
      font: types.rgba({ r: 255, g: 255, b: 255, a: 1 }),
    }),
    border: types.compound({
      size: types.number(0),
      color: types.rgba({ r: 255, g: 255, b: 255, a: 1 }),
      radius: types.string(''),
    }),
    title: types.string('這邊應該要代入內容'),
    content: types.string('這邊應該要代入標題'),
    opacity: types.number(0, {
      range: [0, 1],
      nudgeMultiplier: 0.01,
    }),
    padding: types.number(0, {
      range: [0, 50],
    }),
    visible: types.boolean(true),
  },

  IMAGE: {
    position: {
      x: 0,
      y: 0,
    },
    bgColor: types.string('#ffffff'),
  },
};
