import studio from '@theatre/studio';
import { useState, useEffect } from 'react';
import Stage from '../components/stage';
import EditPanel from '../components/edit-panel';
import { getProject } from '@theatre/core';
import { renderFontObject, renderImageObject, renderBgObject } from '../utils';

export default function Playground() {
  studio.initialize();
  studio.ui.restore();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // get objectJSON in local-storage
  const objJson = localStorage.getItem('theatre-data');
  const objData = objJson ? JSON.parse(objJson) : [];

  const [storedData, setStoredData] = useState(objData);

  useEffect(() => {
    if (storedData.length > 0) {
      renderFontObject(storedData, sheet);
      renderImageObject(storedData, sheet);
      renderBgObject(storedData, sheet);
    }
  }, [storedData]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'Escape') {
        hidePanel(); // 调用你想要执行的 hidePanel 函数
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  });

  return (
    <>
      <EditPanel
        sheet={sheet}
        setStoredData={setStoredData}
        storedData={storedData}
      />
      <Stage storedData={storedData} sheet={sheet} />
    </>
  );
}
