import { Routes, Route, BrowserRouter } from 'react-router-dom';
import studio from '@theatre/studio';
import { getProject } from '@theatre/core';
import Playground from './page/playground';
import DemoScroll from './page/demo-scroll';
import DemoVideo from './page/demo-video';
import projectState from './data/state.json';
import { useState, useEffect } from 'react';
import { isEmptyObject } from './utils';

export default function App() {
  const [animateJSON, setAnimateJSON] = useState({});

  useEffect(() => {
    const elements = localStorage.getItem('theatre-0.4.persistent');
    if (elements) {
      setAnimateJSON(elements);
    }
  }, []);

  const state = isEmptyObject(animateJSON) ? {} : { state: animateJSON };

  studio.initialize();
  const project = getProject('Test Project', state);
  const sheet = project.sheet('Scene', 'default');

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path='/'
          element={
            <Playground
              project={project}
              sheet={sheet}
              setAnimateJSON={setAnimateJSON}
              animateJSON={animateJSON}
            />
          }
        />
        <Route
          path='demo-scroll'
          element={<DemoScroll project={project} sheet={sheet} />}
        />
        <Route
          path='demo-video'
          element={<DemoVideo project={project} sheet={sheet} />}
        />
      </Routes>
    </BrowserRouter>
  );
}
