import studio from '@theatre/studio';
import { getProject, types, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import useScrollTrigger from '../hook/useScrollTrigger'; // import the modified useScrollTrigger hook
import projectState from '../json/state.json';

const defaultViewportHeight = '100vh';

// 這是畫面框
const Container = styled.div`
  min-width: 100%;
  height: 100vh;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
  overflow-y: scroll;
  position: relative;
`;

//這是元件框
const Content = styled.div`
  width: 100%;
  height: 100vh;
  outline: 5px solid blue;
  position: absolute;
  top: 0;
  left: 0;
`;

const Fake = styled.div`
  width: 100%;
  height: 200vh;
  background: pink;
`;

const BasicBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

const Test = styled.div`
  width: 100%;
  height: 100vh;
  outline: 3px solid blue;
`;

// 下面參考 scrollable-video ----------------------

//代表滑到這個框框的最上方
const PositionStarter = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const Box100vh = styled.div`
  width: 0;
  position: fixed;
  height: ${defaultViewportHeight};
  left: 0;
  top: 0;
  opacity: 0;
`;

const Section = styled.section`
  box-sizing: border-box;
  * {
    box-sizing: border-box;
  }
  position: relative;
  height: ${(props) =>
    props.isVideoError || props.isVideoLoading || !props.pixel100vh
      ? defaultViewportHeight
      : Math.round(props.videoDuration / props.secondsPer100vh + 1) *
          props.pixel100vh +
        'px'};
  width: ${(props) =>
    props.viewportWidth ? `${props.viewportWidth}px` : defaultSectionWidth};
  max-width: 100vw;
  left: ${(props) => (props.translateX ? `${-1 * props.translateX}px` : 0)};
`;

export default function PlayPage() {
  studio.initialize();
  const config = {};
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  //操作 UI 顯示
  studio.ui.restore();
  //先預設是 pause
  project.ready.then(() => sheet.sequence.pause);

  console.log(sheet.sequence.position);

  const sequenceLength = val(sheet.sequence.pointer.length);
  console.log('sequenceLength', sequenceLength);

  const [preData, setPreData] = useState([]);

  useEffect(() => {
    const storedBoxes = localStorage.getItem('theatre-data');
    if (storedBoxes) {
      setPreData(JSON.parse(storedBoxes));
    }
  }, []);

  useEffect(() => {
    preData
      .filter((item) => item.type === 'box')
      .forEach((item) => {
        const box = sheet.object(item.id, {
          position: {
            x: 0,
            y: 0,
          },
          bgColor: types.string('#ffffff'),
        });

        const element = document.getElementById(item.id);

        box.onValuesChange((newValue) => {
          element.style.left = newValue.position.x + 'px';
          element.style.top = newValue.position.y + 'px';
          element.style.backgroundColor = newValue.bgColor;
        });
      });
  });

  const latestScrollTriggerRef = useScrollTrigger({
    // Pass necessary parameters to the useScrollTrigger hook
    duration: sequenceLength,
    onCreatingScrollTriggerError: console.error,
    onScrollUpdate: (scrollProgress) => {
      sheet.sequence.position = scrollProgress * sequenceLength; // Update the sequence position based on scroll progress
    },
  });

  const positionStarterRef = useRef(null);
  const box100vhRef = useRef(null);

  return (
    <>
      <PositionStarter ref={positionStarterRef} />
      <Box100vh ref={box100vhRef} />
      <Container id='item-container'>
        <Content id='content'>
          {preData.map((box, index) => (
            <BasicBox key={index} id={box.id} style={box.style}>
              {box.content}
            </BasicBox>
          ))}
        </Content>
      </Container>

      {/* <Dimmer>
        錯誤訊息遮罩：'捲動式影片影片載入失敗。請檢查您的網路連線，並重新整理瀏覽器。若您的瀏覽器不支援捲動式影片，請使用較新版
        Firefox、Chrome、Safari、或 Edge 瀏覽器觀看。'
      </Dimmer>

      <Dimmer>
        錯誤訊息遮罩：'您的瀏覽器不支援捲動式影片，請使用較新版 Firefox、Chrome、Safari、或
        Edge 瀏覽器觀看。'
      </Dimmer> */}
    </>
  );
}
