import React from 'react';
import styled from 'styled-components';
import { initialConfig } from '../constants';
import studio from '@theatre/studio';
import { defaultFontStyle, defaultImageStyle } from './shared-style/index';

console.log('defaultFontStyle', defaultFontStyle);

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle}
`;

export default function PlaygroundStage({ storedData, sheet }) {
  const fontElements = storedData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return (
        <FontElement key={index} id={data.id}>
          {data.content}
        </FontElement>
      );
    });

  const imageElements = storedData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      const style = { backgroundImage: `url(${data.url})` };
      return <ImageElement key={index} id={data.id} style={style} />;
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
    </Wrapper>
  );
}
