import studio from '@theatre/studio';
import { getProject, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import TheatreElements from '../components/theatre-elements';

// import { getProject, types } from '@theatre/core';
import projectState from '../json/state.json';

const defaultViewportHeight = '100vh';

const ViewBox = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  outline: 5px solid red;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100%;
  outline: 5px solid red;
  overflow-y: auto;
  padding-right: 15px; //to hide scroll-bar
`;

const ScrollHeight = styled.div`
  background: pink;
  width: 100%;
  height: 200vh;
`;

// 下面參考 scrollable-video ---------------------------------

//代表滑到這個框框的最上方
const PositionStarter = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

// 對位用，對齊左上角
// const Box100vh = styled.div`
//   width: 0;
//   position: fixed;
//   height: ${defaultViewportHeight};
//   left: 0;
//   top: 0;
//   background: black;
//   z-index: 100000;
//   /* opacity: 0; */
// `;

const Other = styled.div`
  width: 100%;
  height: 100vh;
`;

export default function ScrollDemo() {
  studio.initialize();
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.hide();
  project.ready.then(() => sheet.sequence.pause);

  const containerRef = useRef(null);
  const positionStarterRef = useRef(null);

  //滑動的離頂端有多少距離
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const container = containerRef.current;
    const handleScroll = () => {
      const currentPosition = container.scrollTop;
      setScrollPosition(currentPosition);
    };

    container.addEventListener('scroll', handleScroll);

    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const sequenceLength = val(sheet.sequence.pointer.length);
  // 透過 scrollPosition 去決定 sequence 的位置
  useEffect(() => {
    const newPosition = (scrollPosition / 2000) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  const [scrollEnabled, setScrollEnabled] = useState(false);

  console.log('scrollEnabled', scrollEnabled);

  useEffect(() => {
    const positionStarter = positionStarterRef.current;

    // const handleScrollEnable = () => {
    //   const positionStarterRect = positionStarter.getBoundingClientRect();

    //   console.log('positionStarterRect.top', positionStarterRect.top);
    //   console.log('positionStarterRect.left', positionStarterRect.left);

    //   console.log(
    //     ' positionStarterRect.top === 0 && positionStarterRect.left === 0',
    //     positionStarterRect.top <= 0 && positionStarterRect.left <= 0
    //   );

    //   setScrollEnabled(
    //     positionStarterRect.top === 0 && positionStarterRect.left === 0
    //   );
    // };

    const handleScrollEnable = () => {
      const positionStarterRect = positionStarter.getBoundingClientRect();

      const isAtTop = positionStarterRect.top <= 0;
      const isAtBottom =
        window.innerHeight + containerRef.current.scrollTop >=
        containerRef.current.scrollHeight;

      console.log('isAtTop', isAtTop);

      console.log('isAtBottom', isAtBottom);

      setScrollEnabled(isAtTop && !isAtBottom);
    };

    handleScrollEnable(); // Check initial visibility
    window.addEventListener('scroll', handleScrollEnable);

    return () => {
      window.removeEventListener('scroll', handleScrollEnable);
    };
  }, []);

  useEffect(() => {
    document.body.style.overflow = scrollEnabled ? 'hidden' : 'auto';

    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [scrollEnabled]);

  return (
    <>
      <Other />
      <PositionStarter ref={positionStarterRef} id='starter' />
      <ViewBox>
        <TheatreContainer
          id='item-container'
          ref={containerRef}
          style={{ overflow: scrollEnabled ? 'auto' : 'hidden' }}
        >
          <TheatreElements sheet={sheet} />
          <ScrollHeight />
          <PositionEnd ref={positionEndRef} id='end' />
        </TheatreContainer>
      </ViewBox>

      <Other />
    </>
  );
}
