import React, { useState } from 'react';
import styled from 'styled-components';

const RangeBar = styled.div`
  /* position: relative; */
  position: fixed;
  right: 50px;
  bottom: 50px;
  width: 200px;
  height: 20px;
  /* outline: 5px solid red; */
  z-index: 100;

  input {
    width: 100%;
    position: absolute;
    top: 50%;
    z-index: 3;
    transform: translateY(-50%);
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    height: 4px;
    opacity: 0;
    margin: 0;
  }

  .range-slider_thumb {
    width: 20px;
    height: 20px;
    border: 0.6px solid #303030;
    border-radius: 50%;
    position: absolute;
    left: ${({ thumbPosition }) => thumbPosition}px;
    top: 50%;
    transform: translateY(-50%);
    background-color: #f4f4f4;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: 700;
    font-size: 4px;
    color: #303030;
    z-index: 2;
  }
  .range-slider_line {
    height: 0.5vmin;
    width: 100%;
    background-color: #e1e1e1;
    top: 50%;
    transform: translateY(-50%);
    left: 0;
    position: absolute;
    z-index: 1;
  }

  .range-slider_line-fill {
    position: absolute;
    height: 0.5vmin;
    width: ${({ fillWidth }) => fillWidth}%;
    background-color: #303030;
  }
`;

export default function RangeSlider() {
  const [sliderValue, setSliderValue] = useState(20);

  function handleChange(event) {
    const value = parseInt(event.target.value, 10);
    setSliderValue(value);
    setStageSize(value);
  }

  return (
    <RangeBar thumbPosition={(sliderValue / 100) * 200} fillWidth={sliderValue}>
      <div className='range-slider_thumb'>{sliderValue}</div>
      <div className='range-slider_line'>
        <div className='range-slider_line-fill'></div>
      </div>
      <input
        className='range-slider_input'
        type='range'
        value={sliderValue}
        min='0'
        max='100'
        onChange={handleChange}
      />
    </RangeBar>
  );
}
