import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 50%;
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;

  textarea {
    width: 100%;
    height: 200px;
    margin-bottom: 10px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  setAnimateJSON,
  animateJSON,
  elementJSON,
  setElementJSON,
}) {
  return (
    <Wrapper>
      <Form>
        <h2>JSON 資料</h2>

        <span>元件 JSON</span>
        <textarea
          placeholder='元件JSON'
          value={elementJSON}
          onChange={(e) => setElementJSON(e.target.value)}
        />

        <span>動畫 JSON</span>
        <textarea
          placeholder='動畫JSON'
          value={JSON.stringify(animateJSON)}
          onChange={(e) => setAnimateJSON(e.target.value)}
        />

        <button
          onClick={() => {
            setShowJSONLightBox(false);
            localStorage.setItem('theatre-data', elementJSON);
            localStorage.setItem('theatre-0.4.persistent', animateJSON);
            window.location.reload();
          }}
        >
          儲存修改
        </button>

        <button
          onClick={() => {
            setShowJSONLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
