import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  padding: 10px;
  background: #c1dafe;
  max-width: 100px;

  button {
    padding: 5px;
    background: white;
    margin: 5px;
    cursor: pointer;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: '新增 - 背景',
    className: 'add-background',
    onClick: () => {},
  },
  {
    label: '新增 - 文字',
    className: 'add-font',
    onClick: ({ setShowFontLightBox }) => {
      setShowFontLightBox(true);
    },
  },
  {
    label: '新增 - 圖片',
    className: 'add-image',
    onClick: ({ setShowFontLightBox }) => {
      setShowFontLightBox(true);
    },
  },
  {
    label: '刪除元件',
    className: 'delete-element',
    onClick: () => {},
  },
];

export default function ElementButtons({ setShowFontLightBox }) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          className={button.className}
          onClick={() => button.onClick({ setShowFontLightBox })}
        >
          {button.label}
        </button>
      ))}
    </Wrapper>
  );
}
