import studio from '@theatre/studio';
import { getProject, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import TheatreElements from '../components/theatre-elements';
import { useInView } from 'react-intersection-observer';

// import { getProject, types } from '@theatre/core';
import projectState from '../json/state.json';

const defaultViewportHeight = '100vh';

const ViewBox = styled.div`
  position: relative;
  width: 100%;
  height: 3000px;
  min-height: 100vh;
  outline: 3px solid blue;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100vh;
  outline: 1px solid red;

  position: sticky;
  top: 0;
  left: 0;
`;

// 下面參考 scrollable-video ---------------------------------

//代表滑到這個框框的最上方
const PositionStarter = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const PositionFinisher = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const Other = styled.div`
  width: 100%;
  height: 100vh;
`;

const Test = styled.div`
  width: 25px;
  height: 25px;
  outline: 1px solid red;
  position: sticky;
  top: 0;
  left: 0;
  background: red;
`;

export default function ScrollDemo() {
  studio.initialize();
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.hide();
  project.ready.then(() => sheet.sequence.pause);

  const sequenceLength = val(sheet.sequence.pointer.length);

  // --------------------------------------------------------

  const testRef = useRef(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  console.log('scrollPosition', scrollPosition);

  // useEffect(() => {
  //   const handleScroll = () => {
  //     const testElement = testRef.current;
  //     const viewboxElement = testElement.closest('.ViewBox');

  //     const testRect = testElement.getBoundingClientRect();
  //     const viewboxRect = viewboxElement.getBoundingClientRect();

  //     const distance = testRect.top - viewboxRect.top;
  //     setScrollPosition(distance);
  //   };

  //   window.addEventListener('scroll', handleScroll);

  //   return () => {
  //     window.removeEventListener('scroll', handleScroll);
  //   };
  // }, []);

  // useEffect(() => {
  //   // 3000 等同於 ScrollHeight 元件的高度
  //   const newPosition = (scrollPosition / 3000) * sequenceLength;
  //   sheet.sequence.position = newPosition;
  // }, [scrollPosition]);

  // ----- 上面這段是絕對可用的，勿動 ---------------------------------------------------

  const positionStarterRef = useRef(null);
  const positionFinisherRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      // 获取 PositionStarter 和 PositionFinisher 元素的位置信息
      const positionStarterElement = positionStarterRef.current;
      const positionFinisherElement = positionFinisherRef.current;
      const positionStarterRect =
        positionStarterElement.getBoundingClientRect();
      const positionFinisherRect =
        positionFinisherElement.getBoundingClientRect();

      // 获取滚动条的垂直位置
      const scrollPosition = window.pageYOffset;

      if (
        scrollPosition >= positionStarterRect.top &&
        scrollPosition <= positionFinisherRect.top
      ) {
        // 滑动到 PositionStarter 和 PositionFinisher 之间时添加滚动事件监听器
        const handleScroll = () => {
          // 处理滚动事件的逻辑
        };

        window.addEventListener('scroll', handleScroll);
      } else {
        // 滑动到 PositionStarter 和 PositionFinisher 之外时移除滚动事件监听器
        window.removeEventListener('scroll', handleScroll);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <Other />
      <PositionStarter ref={positionStarterRef} />
      <ViewBox className='ViewBox'>
        <TheatreContainer id='theatre-container' ref={testRef}>
          <TheatreElements sheet={sheet} />
        </TheatreContainer>
      </ViewBox>
      <PositionFinisher ref={positionFinisherRef} />
      <Other />
    </>
  );
}
